import React, {useState} from'react'
import {Button,Form,InputGroup,FormControl} from 'react-bootstrap';
import './register.css';


function Register() {
  const [user,setUser]=useState({
     name: "",email:"",phone:"",password:"",cpassword:""
      });
       let name,value;
      const handleInputs=(e)=>{
        console.log(e);
        name=e.target.name;
        value=e.target.value;
        setUser({...user,[name]:value});
      }
  return (
  <><div container>
    <h1 className="heading">REGISTER YOURSELF WITH ANNANDATA</h1>
    <Form >
      <div className="Registration-form">
      <div className="form-items">
    <FormControl isRequired className="Name" controlId="formBasicName">
      <InputGroup>
        <Form.Label>User Name</Form.Label>
        <Input type="name" placeholder="Enter your Full Name" value={user.name}
        onChange={handleInputs} />
      </InputGroup>
      </FormControl>
      <InputGroup className="Email" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label>
        <Form.Control type="email" placeholder="Enter email" 
        value={user.email}
        onChange={handleInputs} />
      </InputGroup>
      <InputGroup className="Phone" controlId="formBasicNumber">
        <Form.Label>Phone Number</Form.Label>
       <Form.control type="number" placeholder="Enter your phone number" 
        value={user.phone}
        onChange={handleInputs}/>
    
        <Form.Text className="text-muted">
          Your contact number should be 10 digits long.
        </Form.Text>
      </InputGroup>

      <InputGroup className="Password" controlId="formBasicPassword">
        <Form.Label>Password</Form.Label>
       <Form.control type="password" placeholder="Enter Password" 
        value={user.password}
        onChange={handleInputs}/>
   
      </InputGroup>
      <InputGroup className="CPassword" controlId="formBasicConfirmPassword">
        <Form.Label>Confirm Password</Form.Label>
      <Form.Control type="password" placeholder=" Confirm Password"  
        value={user.cpassword}
        onChange={handleInputs} />
       
        <Form.Text className="text-muted">
          confirm password must match your set password.
        </Form.Text>
      </InputGroup>
      <Button variant="primary" type="submit" className="button">
        Submit
      </Button>
      <p className="link2"> <a href="/Login">
            If already Registered,Back to Login!</a></p>
      </div>
      </div>
      </Form>
    <div className='backgroundimg2'>
      <img alt=" "></img>
    </div>
    
    </div>
    </>
  )
}

export default Register;
