import  React,{useState} from'react'
import Form from 'react-bootstrap/Form';

import Button from 'react-bootstrap/Button';


import './register.css';


const Register=()=> {

  const [user,setUser]=useState({
      name:"",
      email:"",
      phone:"",
      password:"",
      cpassword:""
  });
  let name,value;
  const handleInputs=(e)=>{
      name=e.target.name;
      value=e.target.value
      setUser({...user,[name]:value});
  }
  const PostData=async (e)=>{
    e.preventDefault()
    const {name,email,phone,password,cpassword}=user;
    const res=await fetch("/Register",{
        method:"POST",
        headers:{
            "Content-Type":"application/json"
        },
        body:JSON.stringify({
            name,email,phone,password,cpassword
        })
    })
    const data=await res.json();
    if(data.status===422||!data){
        window.alert("Invalid registration");
        console.log("Invalid registration")
    }
    else{
        console.log(res.status)
        window.alert("Successful registration");
        console.log("Successful registration")
        
    }
}
 
  return (
  <>
    <h1 className="heading">REGISTER YOURSELF WITH ANNANDATA</h1>
    <form method="POST" >
      <div className="Registration-form">
      <div className="form-items">
       
      <Form.Group className="Name" controlId="formBasicName">
        <Form.Label>Name</Form.Label>
        <Form.Control type="name" name="name" placeholder="Enter your name" value={user.name}onChange={handleInputs} 
        />
      </Form.Group>

      <Form.Group className="Email" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label>
        <Form.Control  type="email" name="email" placeholder="Enter email" value={user.email}
        onChange={handleInputs} 
        />
      </Form.Group>

      <Form.Group className="Phone" controlId="formBasicPhone">
        <Form.Label>Contact</Form.Label>
        <Form.Control type="number" name="phone" placeholder="Enter your phone number" value={user.phone}
        onChange={handleInputs} 
        />
        <Form.Text className="text-muted">
          your contact number should be 10 digits long.
        </Form.Text>
      </Form.Group>

      <Form.Group className="Password" controlId="formBasicpassword">
        <Form.Label>Password</Form.Label>
        <Form.Control  type="password" name="password" placeholder="Enter your password" value={user.password}
        onChange={handleInputs} 
        />
      </Form.Group>

      <Form.Group className="Cpassword" controlId="formBasicCpassword">
        <Form.Label>Confirm password </Form.Label>
        <Form.Control  type="password" name="cpassword" placeholder="Re-enter your password" value={user.cpassword}
        onChange={handleInputs} 
        />
        <Form.Text className="text-muted">
          confirm password should match with your set password.
        </Form.Text>
      </Form.Group>


      <Button variant="primary" type="submit" className="button" onClick={PostData} >
        Submit
      </Button>
      <p className="link2"> <a href="/Login">
            If already Registered,Back to Login!</a></p>
      </div>
      </div>
      </form>
    <div className='backgroundimg2'>
      <img alt=" "></img>
    </div>
    </>
  )
}

export default Register;
