import  {React,useState} from'react'
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';
import InputGroup from 'react-bootstrap/InputGroup';
import './register.css';


const Register=()=> {
  var history=useHistory()
  const [user,setUser]=useState({
      name:null,
      email:null,
      phone:null,
      password:null,
      cpassword:null
  })
  let name,value
  const handleInputs=(e)=>{

      name=e.target.name;
      value=e.target.value
      if(!name||!value)
      window.alert("ERROR")
      else
      setUser({...user,[name]:value})
  }
  const PostData=async (e)=>{
      e.preventDefault()
      const {name,email,phone,password,cpassword}=user;
      const res=await fetch("/Register",{
          method:"POST",
          headers:{
              "Content-Type":"application/json"
          },
          body:JSON.stringify({
              name,email,phone,password,cpassword
          })
      })
      const data=await res.json();
      if(res.status===400||!data){
          window.alert("Invalid registration");
          console.log("Invalid registration")
      }
      else{
          console.log(res.status)
          window.alert("Successful registration");
          console.log("Successful registration")
          console.log(history)
      }
  }
  return (
  <>
    <h1 className="heading">REGISTER YOURSELF WITH ANNANDATA</h1>
    <form method="POST"  >
      <div className="Registration-form">
      <div className="form-items">
       
      <Form.Group className="Name" controlId="formBasicName">
        <Form.Label>Name</Form.Label>
        <Form.Control type="name" placeholder="Enter your name" value={user.name}onChange={handleInputs} 
        />
        <Form.Text className="text-muted">
          We'll never share your email with anyone else.
        </Form.Text>
      </Form.Group>

      <Form.Group className="Email" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label>
        <Form.Control  type="email" placeholder="Enter email" value={user.email}
        onChange={handleInputs} 
        />
        <Form.Text className="text-muted">
          We'll never share your email with anyone else.
        </Form.Text>
      </Form.Group>

      <Form.Group className="Phone" controlId="formBasicPhone">
        <Form.Label>Contact</Form.Label>
        <Form.Control type="number" placeholder="Enter your phone number" value={user.phone}
        onChange={handleInputs} 
        />
        <Form.Text className="text-muted">
          your contact number should be 10 digits long.
        </Form.Text>
      </Form.Group>

      <Form.Group className="Password" controlId="formBasicpassword">
        <Form.Label>Password</Form.Label>
        <Form.Control  type="password" placeholder="Enter your password" value={user.password}
        onChange={handleInputs} 
        />
      </Form.Group>

      <Form.Group className="Cpassword" controlId="formBasicCpassword">
        <Form.Label>Confirm password </Form.Label>
        <Form.Control  type="password" placeholder="Re-enter your password" value={user.cpassword}
        onChange={handleInputs} 
        />
        <Form.Text className="text-muted">
          confirm password should match with your set password.
        </Form.Text>
      </Form.Group>


      <Button variant="primary" type="submit" className="button">
        Submit
      </Button>
      <p className="link2"> <a href="/Login">
            If already Registered,Back to Login!</a></p>
      </div>
      </div>
      </form>
    <div className='backgroundimg2'>
      <img alt=" "></img>
    </div>
    </>
  )
}

export default Register;
