import React from'react'
import Button from 'react-bootstrap/Button';
import {Form} from 'react-bootstrap/Form';
import './register.css';
import {useState} from "react-router-dom"

function Register() {
  const [user,setUser]=useState({
     name: "",email:"",phone:"",password:"",cpassword:""
      });
       let name,value;
      const handleInputs=(e)=>{
        console.log(e);
        name=e.target.name;
        value=e.target.value;
        setUser({...user,[name]:value});
      }
  return (
  <><div container>
    <h1 className="heading">REGISTER YOURSELF WITH ANNANDATA</h1>
    <form >
      <div className="Registration-form">
      <div className="form-items">
    <Form.Group className="Name" controlId="formBasicName">
        <Form.Label>User Name</Form.Label>
        <Form.Control isRequired type="name" placeholder="Enter your Full Name" value={user.name}
        onChange={handleInputs} />
      </Form.Group>
      <Form.Group className="Email" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label>
        <Form.Control isRequired type="email" placeholder="Enter email" 
        value={user.email}
        onChange={handleInputs} ></Form.Control>
      </Form.Group>
      <Form.Group className="Phone" controlId="formBasicNumber">
        <Form.Label>Phone Number</Form.Label>
        <Form.Control isRequired type="number" placeholder="Enter your phone number" 
        value={user.phone}
        onChange={handleInputs}/>
        <Form.Text className="text-muted">
          Your contact number should be 10 digits long.
        </Form.Text>
      </Form.Group>

      <Form.Group className="Password" controlId="formBasicPassword">
        <Form.Label>Password</Form.Label>
        <Form.Control isRequired type="password" placeholder="Enter Password" 
        value={user.password}
        onChange={handleInputs}/>
        
      </Form.Group>
      <Form.Group className="CPassword" controlId="formBasicConfirmPassword">
        <Form.Label>Confirm Password</Form.Label>
        <Form.Control  isRequired  type="password" placeholder=" Confirm Password"  
        value={user.cpassword}
        onChange={handleInputs} />
        <Form.Text className="text-muted">
          confirm password must match your set password.
        </Form.Text>
      </Form.Group>
      <Button variant="primary" type="submit" className="button">
        Submit
      </Button>
      <p className="link2"> <a href="/Login">
            If already Registered,Back to Login!</a></p>
      </div>
      </div>
      </form>
    <div className='backgroundimg2'>
      <img ></img>
    </div>
    
    </div>
    </>
  )
}

export default Register;
